import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';

var stompClient = null;

const ChatRoom = () => {
    const [privateChats, setPrivateChats] = useState(new Map());
    const [publicChats, setPublicChats] = useState([]);
    const [tab, setTab] = useState("CHATROOM");
    const [userData, setUserData] = useState({
        username: '',
        receivername: '',
        connected: false,
        message: '',
        editing: null, // For editing a message
        editMessageContent: '' // For storing the content of the message being edited
    });

    useEffect(() => {
        console.log('User data:', userData);
    }, [userData]);

    const connect = () => {
        let Sock = new SockJS('http://localhost:9092/ws'); // Ensure this matches your WebSocket endpoint
        stompClient = over(Sock);
        stompClient.connect({}, onConnected, onError);
    }

    const onConnected = () => {
        setUserData({ ...userData, "connected": true });
        stompClient.subscribe('/chatroom/public', onMessageReceived);
        stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage);
        userJoin();
    }

    const userJoin = () => {
        var chatMessage = {
            senderName: userData.username,
            status: "JOIN"
        };
        stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
    }

    const onMessageReceived = (payload) => {
        var payloadData = JSON.parse(payload.body);
        console.log('Received message:', payloadData);
        switch (payloadData.status) {
            case "JOIN":
                if (!privateChats.get(payloadData.senderName)) {
                    privateChats.set(payloadData.senderName, []);
                    setPrivateChats(new Map(privateChats));
                }
                break;
            case "MESSAGE":
                setPublicChats(prevChats => {
                    // Avoid adding duplicate messages
                    if (!prevChats.find(chat => chat.id === payloadData.id)) {
                        return [...prevChats, payloadData];
                    }
                    return prevChats;
                });
                break;
            case "EDITED":
                setPublicChats(prevChats => prevChats.map(chat =>
                    chat.id === payloadData.id ? payloadData : chat
                ));
                // Also update private chats if needed
                if (privateChats.has(payloadData.senderName)) {
                    setPrivateChats(prevChats => {
                        const updatedPrivateChats = new Map(prevChats);
                        const updatedChats = updatedPrivateChats.get(payloadData.senderName).map(chat =>
                            chat.id === payloadData.id ? payloadData : chat
                        );
                        updatedPrivateChats.set(payloadData.senderName, updatedChats);
                        return updatedPrivateChats;
                    });
                }
                break;
            default:
                break;
        }
    }

    const onPrivateMessage = (payload) => {
        var payloadData = JSON.parse(payload.body);
        console.log('Received private message:', payloadData);
        setPrivateChats(prevChats => {
            const updatedChats = new Map(prevChats);
            if (updatedChats.has(payloadData.senderName)) {
                updatedChats.get(payloadData.senderName).push(payloadData);
            } else {
                updatedChats.set(payloadData.senderName, [payloadData]);
            }
            return updatedChats;
        });
    }

    const onError = (err) => {
        console.log('WebSocket error:', err);
    }

    const handleMessage = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, "message": value });
    }

    const handleEditMessage = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, "editMessageContent": value });
    }

    const sendValue = () => {
        if (stompClient) {
            var chatMessage = {
                senderName: userData.username,
                message: userData.message,
                status: "MESSAGE"
            };
            stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
            setUserData({ ...userData, "message": "" });
        }
    }

    const sendPrivateValue = () => {
        if (stompClient) {
            var chatMessage = {
                senderName: userData.username,
                receiverName: tab,
                message: userData.message,
                status: "MESSAGE"
            };
            if (userData.username !== tab) {
                setPrivateChats(prevChats => {
                    const updatedChats = new Map(prevChats);
                    if (updatedChats.has(tab)) {
                        updatedChats.get(tab).push(chatMessage);
                    } else {
                        updatedChats.set(tab, [chatMessage]);
                    }
                    return updatedChats;
                });
            }
            stompClient.send("/app/private-message", {}, JSON.stringify(chatMessage));
            setUserData({ ...userData, "message": "" });
        }
    }

    const handleUsername = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, "username": value });
    }

    const registerUser = () => {
        connect();
    }

    const startEditing = (messageId, messageContent) => {
        setUserData({ ...userData, editing: messageId, editMessageContent: messageContent });
    }

    const saveEditedMessage = () => {
        if (stompClient && userData.editing) {
            var updatedMessage = {
                id: userData.editing,
                senderName: userData.username,
                message: userData.editMessageContent,
                status: "EDITED"
            };
            console.log('Sending edited message:', updatedMessage);
            stompClient.send("/app/edit-message", {}, JSON.stringify(updatedMessage));

            // Update local state
            setPublicChats(prevChats => prevChats.map(chat =>
                chat.id === userData.editing ? { ...chat, message: userData.editMessageContent, lastModified: new Date() } : chat
            ));

            // Also update private chats if needed
            if (privateChats.has(tab)) {
                setPrivateChats(prevChats => {
                    const updatedChats = new Map(prevChats);
                    const updatedPrivateChats = updatedChats.get(tab).map(chat =>
                        chat.id === userData.editing ? { ...chat, message: userData.editMessageContent, lastModified: new Date() } : chat
                    );
                    updatedChats.set(tab, updatedPrivateChats);
                    return updatedChats;
                });
            }

            setUserData({ ...userData, editing: null, editMessageContent: '' });
        }
    }

    return (
        <div className="container">
            {userData.connected ?
                <div className="chat-box">
                    <div className="member-list">
                        <ul>
                            <li onClick={() => { setTab("CHATROOM") }} className={`member ${tab === "CHATROOM" && "active"}`}>Chatroom</li>
                            {[...privateChats.keys()].map((name, index) => (
                                <li onClick={() => { setTab(name) }} className={`member ${tab === name && "active"}`} key={index}>{name}</li>
                            ))}
                        </ul>
                    </div>
                    {tab === "CHATROOM" && <div className="chat-content">
                        <ul className="chat-messages">
                            {publicChats.map((chat, index) => (
                                <li className={`message ${chat.senderName === userData.username && "self"}`} key={chat.id}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">
                                        {userData.editing === chat.id ? (
                                            <div>
                                                <input type="text" value={userData.editMessageContent} onChange={handleEditMessage} />
                                                <button onClick={saveEditedMessage}>Save</button>
                                            </div>
                                        ) : (
                                            chat.message
                                        )}
                                    </div>
                                    {chat.senderName === userData.username && <div className="avatar self">{chat.senderName}</div>}
                                    {chat.senderName === userData.username && !userData.editing && (
                                        <button onClick={() => startEditing(chat.id, chat.message)}>Edit</button>
                                    )}
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="Enter your message" value={userData.message} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={sendValue}>Send</button>
                        </div>
                    </div>}
                    {tab !== "CHATROOM" && <div className="chat-content">
                        <ul className="chat-messages">
                            {(privateChats.get(tab) || []).map((chat, index) => (
                                <li className={`message ${chat.senderName === userData.username && "self"}`} key={chat.id}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">
                                        {userData.editing === chat.id ? (
                                            <div>
                                                <input type="text" value={userData.editMessageContent} onChange={handleEditMessage} />
                                                <button onClick={saveEditedMessage}>Save</button>
                                            </div>
                                        ) : (
                                            chat.message
                                        )}
                                    </div>
                                    {chat.senderName === userData.username && <div className="avatar self">{chat.senderName}</div>}
                                    {chat.senderName === userData.username && !userData.editing && (
                                        <button onClick={() => startEditing(chat.id, chat.message)}>Edit</button>
                                    )}
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="Enter your message" value={userData.message} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={sendPrivateValue}>Send</button>
                        </div>
                    </div>}
                </div>
                :
                <div className="login-form">
                    <input type="text" className="username-input" placeholder="Enter your username" value={userData.username} onChange={handleUsername} />
                    <button type="button" className="connect-button" onClick={registerUser}>Connect</button>
                </div>
            }
        </div>
    );
}

export default ChatRoom;
