import React, { useEffect, useState } from 'react';
import { over } from 'stompjs'; // STOMP is a simple text-oriented messaging protocol
import SockJS from 'sockjs-client'; // SockJS is a JavaScript library for providing WebSocket-like objects

// Global variable to store the STOMP client
var stompClient = null;

const ChatRoom = () => {
    // State to manage private chats, public chats, current tab, and user data
    const [privateChats, setPrivateChats] = useState(new Map());
    const [publicChats, setPublicChats] = useState([]);
    const [tab, setTab] = useState("CHATROOM");
    const [userData, setUserData] = useState({
        username: '', // Store the username of the user
        receivername: '', // Store the name of the user you are sending a private message to
        connected: false, // Track if the user is connected to the chat
        message: '', // Store the message the user wants to send
        editing: null, // Store the ID of the message being edited
        editMessageContent: '' // Store the content of the message being edited
    });

    // Debugging: Log user data whenever it changes
    useEffect(() => {
        console.log('User data:', userData);
    }, [userData]);

    // Function to connect to the chat server
    const connect = () => {
        let Sock = new SockJS('http://localhost:9092/ws'); // Create a new SockJS connection
        stompClient = over(Sock); // Use STOMP over the SockJS connection
        stompClient.connect({}, onConnected, onError); // Connect with success and error callbacks
    }

    // Callback function when connected successfully
    const onConnected = () => {
        setUserData({ ...userData, "connected": true }); // Update state to show the user is connected
        stompClient.subscribe('/chatroom/public', onMessageReceived); // Subscribe to public messages
        stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage); // Subscribe to private messages
        userJoin(); // Announce that the user has joined
    }

    // Function to notify the server that the user has joined
    const userJoin = () => {
        var chatMessage = {
            senderName: userData.username,
            status: "JOIN" // Message type is "JOIN"
        };
        stompClient.send("/app/message", {}, JSON.stringify(chatMessage)); // Send the join message
    }

    // Function to handle received public messages
    const onMessageReceived = (payload) => {
        var payloadData = JSON.parse(payload.body); // Parse the received message
        console.log('Received message:', payloadData);
        switch (payloadData.status) {
            case "JOIN":
                if (!privateChats.get(payloadData.senderName)) {
                    privateChats.set(payloadData.senderName, []); // Add the new user to private chats
                    setPrivateChats(new Map(privateChats));
                }
                break;
            case "MESSAGE":
                setPublicChats(prevChats => [...prevChats, payloadData]); // Add the new message to the public chat
                break;
            case "EDITED":
                const updatedPublicChats = publicChats.map(chat =>
                    chat.id === payloadData.id ? payloadData : chat // Update the edited message in public chat
                );
                setPublicChats(updatedPublicChats);

                // Also update private chats if the edited message is in a private chat
                if (privateChats.has(payloadData.senderName)) {
                    const updatedPrivateChats = privateChats.get(payloadData.senderName).map(chat =>
                        chat.id === payloadData.id ? payloadData : chat
                    );
                    privateChats.set(payloadData.senderName, updatedPrivateChats);
                    setPrivateChats(new Map(privateChats));
                }
                break;
            default:
                break;
        }
    }

    // Function to handle received private messages
    const onPrivateMessage = (payload) => {
        var payloadData = JSON.parse(payload.body); // Parse the received message
        console.log('Received private message:', payloadData);
        if (privateChats.get(payloadData.senderName)) {
            privateChats.get(payloadData.senderName).push(payloadData); // Add the message to the correct private chat
            setPrivateChats(new Map(privateChats));
        } else {
            let list = [];
            list.push(payloadData);
            privateChats.set(payloadData.senderName, list); // Create a new private chat if it doesn't exist
            setPrivateChats(new Map(privateChats));
        }
    }

    // Function to handle errors during connection
    const onError = (err) => {
        console.log('WebSocket error:', err);
    }

    // Function to handle the message input field
    const handleMessage = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, "message": value }); // Update the message in state as the user types
    }

    // Function to handle the message edit input field
    const handleEditMessage = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, "editMessageContent": value }); // Update the edited message content in state
    }

    // Function to send a public message
    const sendValue = () => {
        if (stompClient) {
            var chatMessage = {
                senderName: userData.username,
                message: userData.message,
                status: "MESSAGE" // Message type is "MESSAGE"
            };
            stompClient.send("/app/message", {}, JSON.stringify(chatMessage)); // Send the message
            setUserData({ ...userData, "message": "" }); // Clear the message input field
        }
    }

    // Function to send a private message
    const sendPrivateValue = () => {
        if (stompClient) {
            var chatMessage = {
                senderName: userData.username,
                receiverName: tab,
                message: userData.message,
                status: "MESSAGE" // Message type is "MESSAGE"
            };
            if (userData.username !== tab) {
                privateChats.get(tab).push(chatMessage); // Add the message to the private chat
                setPrivateChats(new Map(privateChats));
            }
            stompClient.send("/app/private-message", {}, JSON.stringify(chatMessage)); // Send the private message
            setUserData({ ...userData, "message": "" }); // Clear the message input field
        }
    }

    // Function to handle the username input field
    const handleUsername = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, "username": value }); // Update the username in state
    }

    // Function to start the connection when the user clicks "Connect"
    const registerUser = () => {
        connect(); // Call the connect function
    }

    // Function to start editing a message
    const startEditing = (messageId, messageContent) => {
        setUserData({ ...userData, editing: messageId, editMessageContent: messageContent }); // Store the ID and content of the message being edited
    }

    // Function to save the edited message
    const saveEditedMessage = () => {
        if (stompClient && userData.editing) {
            var updatedMessage = {
                id: userData.editing,
                senderName: userData.username,
                message: userData.editMessageContent,
                status: "EDITED" // Message type is "EDITED"
            };
            console.log('Sending edited message:', updatedMessage);
            stompClient.send("/app/edit-message", {}, JSON.stringify(updatedMessage)); // Send the edited message

            // Update the message in public chat
            const updatedPublicChats = publicChats.map(chat =>
                chat.id === userData.editing ? { ...chat, message: userData.editMessageContent, lastModified: new Date() } : chat
            );
            setPublicChats(updatedPublicChats);

            // Also update the message in private chat if needed
            if (privateChats.has(tab)) {
                const updatedPrivateChats = privateChats.get(tab).map(chat =>
                    chat.id === userData.editing ? { ...chat, message: userData.editMessageContent, lastModified: new Date() } : chat
                );
                privateChats.set(tab, updatedPrivateChats);
                setPrivateChats(new Map(privateChats));
            }

            setUserData({ ...userData, editing: null, editMessageContent: '' }); // Clear the editing state
        }
    }

    return (
        <div className="container">
            {userData.connected ?
                <div className="chat-box">
                    <div className="member-list">
                        <ul>
                            <li onClick={() => { setTab("CHATROOM") }} className={`member ${tab === "CHATROOM" && "active"}`}>Chatroom</li>
                            {[...privateChats.keys()].map((name, index) => (
                                <li onClick={() => { setTab(name) }} className={`member ${tab === name && "active"}`} key={index}>{name}</li>
                            ))}
                        </ul>
                    </div>
                    {tab === "CHATROOM" && <div className="chat-content">
                        <ul className="chat-messages">
                            {publicChats.map((chat, index) => (
                                <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">
                                        {chat.message}
                                        {chat.senderName === userData.username &&
                                            <div className="edit-button" onClick={() => startEditing(chat.id, chat.message)}>Edit</div>}
                                    </div>
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="Enter your message" value={userData.message} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={sendValue}>Send</button>
                        </div>
                    </div>}
                    {tab !== "CHATROOM" && <div className="chat-content">
                        <ul className="chat-messages">
                            {[...privateChats.get(tab)].map((chat, index) => (
                                <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">
                                        {chat.message}
                                        {chat.senderName === userData.username &&
                                            <div className="edit-button" onClick={() => startEditing(chat.id, chat.message)}>Edit</div>}
                                    </div>
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="Enter your message" value={userData.message} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={sendPrivateValue}>Send</button>
                        </div>
                    </div>}
                </div>
                :
                <div className="register">
                    <input
                        id="user-name"
                        placeholder="Enter your name"
                        name="userName"
                        value={userData.username}
                        onChange={handleUsername}
                        margin="normal"
                    />
                    <button type="button" onClick={registerUser}>
                        Connect
                    </button>
                </div>}
        </div>
    )
}

export default ChatRoom;
