import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';

let stompClient = null;

const ChatRoom = () => {
    const [privateChats, setPrivateChats] = useState(new Map());
    const [publicChats, setPublicChats] = useState([]);
    const [tab, setTab] = useState("CHATROOM");
    const [userData, setUserData] = useState({
        username: '',
        receivername: '',
        connected: false,
        message: ''
    });

    useEffect(() => {
        // Clean up WebSocket connection on component unmount
        return () => {
            if (stompClient) {
                stompClient.disconnect();
            }
        };
    }, []);

    const connect = () => {
        if (!stompClient) { // Ensure only one connection is made
            const socket = new SockJS('http://localhost:8080/ws');
            stompClient = over(socket);
            stompClient.connect({}, onConnected, onError);
        }
    };

    const onConnected = () => {
        setUserData(prev => ({ ...prev, connected: true }));
        stompClient.subscribe('/chatroom/public', onMessageReceived);
        stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage);
        userJoin();
    };

    const userJoin = () => {
        const chatMessage = {
            senderName: userData.username,
            status: "JOIN"
        };
        stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
    };

    const onMessageReceived = (payload) => {
        const payloadData = JSON.parse(payload.body);
        switch (payloadData.status) {
            case "JOIN":
                // Add user to private chats if not already present
                setPrivateChats(prevChats => {
                    const updatedChats = new Map(prevChats);
                    if (!updatedChats.has(payloadData.senderName)) {
                        updatedChats.set(payloadData.senderName, []);
                    }
                    return updatedChats;
                });
                break;
            case "MESSAGE":
                setPublicChats(prevChats => {
                    // Avoid adding duplicate messages
                    if (!prevChats.some(chat => chat.message === payloadData.message && chat.senderName === payloadData.senderName)) {
                        return [...prevChats, payloadData];
                    }
                    return prevChats;
                });
                break;
            default:
                break;
        }
    };

    const onPrivateMessage = (payload) => {
        const payloadData = JSON.parse(payload.body);
        setPrivateChats(prevChats => {
            const updatedChats = new Map(prevChats);
            if (updatedChats.has(payloadData.senderName)) {
                updatedChats.get(payloadData.senderName).push(payloadData);
            } else {
                updatedChats.set(payloadData.senderName, [payloadData]);
            }
            return updatedChats;
        });
    };

    const onError = (err) => {
        console.error('WebSocket error:', err);
    };

    const handleMessage = (event) => {
        setUserData(prev => ({ ...prev, message: event.target.value }));
    };

    const sendValue = () => {
        if (stompClient && userData.message.trim()) {
            const chatMessage = {
                senderName: userData.username,
                message: userData.message,
                status: "MESSAGE"
            };
            stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
            setUserData(prev => ({ ...prev, message: "" }));
        }
    };

    const sendPrivateValue = () => {
        if (stompClient && userData.username !== tab && userData.message.trim()) {
            const chatMessage = {
                senderName: userData.username,
                receiverName: tab,
                message: userData.message,
                status: "MESSAGE"
            };
            setPrivateChats(prevChats => {
                const updatedChats = new Map(prevChats);
                if (updatedChats.has(tab)) {
                    updatedChats.get(tab).push(chatMessage);
                } else {
                    updatedChats.set(tab, [chatMessage]);
                }
                return updatedChats;
            });
            stompClient.send("/app/private-message", {}, JSON.stringify(chatMessage));
            setUserData(prev => ({ ...prev, message: "" }));
        }
    };

    const handleUsername = (event) => {
        setUserData(prev => ({ ...prev, username: event.target.value }));
    };

    const registerUser = () => {
        connect();
    };

    return (
        <div className="container">
            {userData.connected ? (
                <div className="chat-box">
                    <div className="member-list">
                        <ul>
                            <li onClick={() => setTab("CHATROOM")} className={`member ${tab === "CHATROOM" ? "active" : ""}`}>Chatroom</li>
                            {[...privateChats.keys()].map((name, index) => (
                                <li onClick={() => setTab(name)} className={`member ${tab === name ? "active" : ""}`} key={index}>{name}</li>
                            ))}
                        </ul>
                    </div>
                    <div className="chat-content">
                        <ul className="chat-messages">
                            {(tab === "CHATROOM" ? publicChats : privateChats.get(tab) || []).map((chat, index) => (
                                <li className={`message ${chat.senderName === userData.username ? "self" : ""}`} key={index}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">{chat.message}</div>
                                    {chat.senderName === userData.username && <div className="avatar self">{chat.senderName}</div>}
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="enter the message" value={userData.message} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={tab === "CHATROOM" ? sendValue : sendPrivateValue}>send</button>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="register">
                    <input
                        id="user-name"
                        placeholder="Enter your name"
                        name="userName"
                        value={userData.username}
                        onChange={handleUsername}
                        margin="normal"
                    />
                    <button type="button" onClick={registerUser}>
                        connect
                    </button>
                </div>
            )}
        </div>
    );
};

export default ChatRoom;
